mapper.select:
select
orderId, orderNumber, orderState, createDate, updateDate, send_addressId, receice_addressId, is_valid,
expressNumber, customer_id, pay_way, user_id
from zto.order


        and orderId = #{orderid}


        and orderNumber = #{ordernumber}


        and orderState = #{orderstate}


        and createDate = #{createdate}


        and updateDate = #{updatedate}


        and send_addressId = #{sendAddressid}


        and receice_addressId = #{receiceAddressid}


        and is_valid = #{isValid}


        and expressNumber = #{expressnumber}


        and customer_id = #{customerId}


        and pay_way = #{payWay}


        and user_id = #{userId}

SELECT
orderId, orderNumber, orderState, createDate, updateDate, send_addressId, receice_addressId, is_valid, expressNumber, customer_id, pay_way, user_id
FROM zto.order
WHERE customer_id =#{customerId}

    AND createDate BETWEEN #{createDate} AND #{createDateTwo}


    and receice_addressId IN(SELECT order_address_id FROM zto.order_address WHERE (send_receive_man=#{sendName} or phone=#{sendName}) and address_type=0 and customer_id = #{customerId})

 AND send_addressId IN (SELECT
 order_address_id
 FROM zto.order_address WHERE customer_id=#{customerId} AND phone=#{phone} AND address_type=1)
limit #{offset}, #{limit}

SELECT orderId, orderNumber, orderState, createDate, updateDate, send_addressId, receice_addressId, is_valid, expressNumber, customer_id, pay_way, user_id FROM zto.order WHERE customer_id =#{customerId}
and (send_addressId IN(SELECT order_address_id FROM zto.order_address WHERE (send_receive_man=#{receiceName} or phone=#{receiceName}) and address_type=1 and customer_id = #{customerId})) or (expressNumber=#{receiceName})
AND receice_addressId IN (SELECT order_address_id FROM zto.order_address WHERE customer_id=#{customerId} AND phone=#{phone} AND address_type=0) limit #{offset}, #{limit}
